<div id="breadcrumb"><div class="<?=$container?>"><div class="breadcrumb"><li><a href="<?=getBaseURL()?>" class="glyphicon glyphicon-home"></a></li><?php $i=1;foreach($row_breadcrumb as $uri_breadcrumb=>$title_breadcrumb){if($uri_breadcrumb!=""&&$title_breadcrumb!=""){if($i<count($row_breadcrumb)){?><li><a href="<?=getURL($uri_breadcrumb)?>"><?=$title_breadcrumb?></a></li><?php }else{?><li class="active"><?=$title_breadcrumb?></li><?php }}$i++;}?><?php if(in_array($template,array("product","post"))):?><div class="view-type"><span class="btn text-primary <?=$template=="product"?"active":""?>" data-target=".<?=$template?>-list" data-add="grid-view" data-remove="list-view"><span class="fa fa-th"></span></span><span class="btn text-primary <?=$template=="post"?"active":""?>" data-target=".<?=$template?>-list" data-add="list-view" data-remove="grid-view"><span class="fa fa-th-list"></span></span></div><script>$(document).ready(function(){$(".view-type .btn").click(function(){$($(this).data("target")).removeClass($(this).data("remove"));$($(this).data("target")).addClass($(this).data("add"));$(".view-type .btn").removeClass("active");$(this).addClass("active");post_resize();product_resize()})});</script><style>.view-type{float:right}.view-type .btn{box-shadow:none!important;padding:7px;border:dotted 1px #ccc;line-height:1;border-radius:0;-webkit-transition:all .3s;-o-transition:all .3s;transition:all .3s}.view-type .btn:first-child{margin-right:-5px;border-right:0}.view-type .btn:hover{color:#424343}.view-type .btn.active{color:#999;cursor:default}.view-type .btn .fa{font-size:20px}.breadcrumb li{line-height:36px}</style><?php endif?></div></div></div><style type="text/css">.breadcrumb{background:transparent;margin-bottom:0;font-weight:600}.breadcrumb>li+li:before{color:inherit;}.breadcrumb>li>.glyphicon-home{margin-right:5px;}</style>